import React, { useState, useCallback } from "react";
import "./Metronome.css";

const Metronome = ({ initialBpm, initialDelay }) => {
    const [bpm, setBpm] = useState(initialBpm);
    const [delay, setDelay] = useState(initialDelay);

    const handleBpmChange = useCallback((event) => {
        setBpm(event.target.value);
    });

    const handleDelayChange = (event) => {
        setDelay(event.target.value);
    }

    return (
        <div>
            <div className='metronome0' />
            <div className='settings'>
                <input id="settings" className="text" placeholder='Insert BPM...' onSearch={handleBpmChange}/><button id="metronomeButtons"/>
                <input id="settings" className="text" placeholder='Insert delay (MS)...' onSearch={handleDelayChange}/><button id="metronomeButtons"/>
            </div>
        </div>
    );
}

export default Metronome;
