import React, { useState, useCallback } from "react";
import ReactPlayer from 'react-player';
import "./VideoPlayer.css";
import Metronome from "../Metronome/Metronome"

const VideoPlayer = ({ videoUrl }) => {

    const [bpm, setBpm] = useState(120);
    const [delay, setDelay] = useState();
    var [playback, setPlayback] = useState(false);
    const [ready, setReady] = useState(false);

    function changeBpm(result) {
        setBpm(result);
    }

    function changeDelay(result) {
        setDelay(result);
    }
    
    function handlePlayback(result) {
        setPlayback(result);
    }

    const isReady = useCallback(() => {
        setPlayback(true);
    }, [])
    

    return (
        <div id="VideoControls">
            <ReactPlayer
                controls
                width="900px" height="540px"
                url={videoUrl}
                onReady={isReady}
                muted={!playback}
                playing={playback}
            />
            <div>
                <Metronome initialBpm={changeBpm} initialDelay={changeDelay} playback={handlePlayback} isReady={ready}/>
            </div>
            
        </div>
        
    )
}

export default VideoPlayer;
