import React, { useState, useCallback } from "react";
import "./Metronome.css";

const Metronome = ({ bpmChange, delayChange }) => {
    const [bpm, setBpm] = useState();
    const [delay, setDelay] = useState();

    const handleBpmChange = useCallback((event) => {
        setBpm(event.target.value)
    });

    const saveBpmChange = useCallback(() => {
        bpmChange(bpm);
    }, [bpmChange, bpm])

    const handleDelayChange = (event) => {
        setDelay(event.target.value);
    }

    const saveDelayChange = useCallback(() => {
        delayChange(delay);
    }, [delayChange, delay])

    return (
        <div>
            <div className='metronome0' />
            <div className='settings'>
                <input id="settings" className="text" placeholder='Insert BPM...' onChange={handleBpmChange}/><button onClick={saveBpmChange}/>
                <input id="settings" className="text" placeholder='Insert delay (MS)...' onChange={handleDelayChange}/><button onClick={saveBpmChange}/>
            </div>
        </div>
    );
}

export default Metronome;
