import React, { useState, useCallback, useEffect } from "react";
import { Buffer } from 'buffer';
import axios from 'axios';
import "./Spotify.css"

/*
This entire file is a work in progress. Sometime in the future i will add functionality of searching Spotify via an API request.
*/

const redirectUri = 'http://localhost:3000/';

const Spotify = ({ onSearch, searchTerm }) => {

    const clientId = "3b34a668dafb405292e73bed43f382d1";
    const clientSecret = "91e1d6b99541441e9774a89b6058a3f3";

    const [term, setTerm] = useState("");

    const handleTermChange = useCallback((event) => {
        setTerm(event.target.value);
        
    }, [])

    const exampleResult = {
        title: "Example Song",
        artist: "Example Artist",
        tempo: 120
    }

    const [token, setToken] = useState('');
    const [songResults, setSongResults] = useState([]);
    const [artistResults, setArtistResults] = useState([]);
    const [bpmResults, setBpmResults] = useState([]);

    useEffect(() => {
        axios("https://accounts.spotify.com/api/token", {
            method: 'POST',
            headers: {
                'Content-Type' : 'application/x-www-form-urlencoded',
                'Authorization' : 'Basic ' + btoa(clientId + ':' + clientSecret)
            },
            data: 'grant_type=client_credentials'
        })
        .then(tokenResponse => {
            console.log(tokenResponse.data.access_token);
            setToken(tokenResponse.data.access_token);
        })
    }, []);




    const searchResults = useCallback(() => {
        onSearch(term);
        const newTerm = term.replace(/ /g, "+");
        setTerm(newTerm);
        getResults();
    }, [onSearch, term]);

    const getResults = () => {
        axios(`https://api.spotify.com/v1/search?q=Green+Day&type=track&limit=5`, { // Green Day is example term, remember to change to term
            method: 'GET',
            headers: { 'Authorization' : 'Bearer ' + token},
        })
        .then(searchResults => {
            console.log(searchResults);
            setSongResults(searchResults.data.tracks.items);
            console.log(songResults);
        })

        
    }

    const results = (
        <table id="Table">
            <thead>
                <tr>
                    <th className="text" id="SongText">Song Name - Artist</th>
                    <th className="text" id="BpmText">BPM</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td className="text" id="SongText">{exampleResult.title} - {exampleResult.artist}</td>
                    <td className="text" id="BpmText">{exampleResult.tempo}</td>
                </tr>
                <tr>
                    <td className="text" id="SongTextBottom">{exampleResult.title} - {exampleResult.artist}</td>
                    <td className="text" id="BpmTextBottom">{exampleResult.tempo}</td>
                </tr>
            </tbody>
        </table>
    )


    /*
        <div id="text">Work in progress.</div>
    */

    return (
        <div>
            <div className="SearchBar">
                <h2></h2>
                <input id="SearchBar" className="text" placeholder="Search Spotify..." onChange={handleTermChange}/>
                <button id="SearchButton" onClick={searchResults}></button>
            </div>
            <div id="SearchResults">
                {results}
            </div>
            
        </div>
    )
}

export default Spotify;
