import React, { useState, useCallback, useRef } from "react";
import "./Metronome.css";

const Metronome = ({ initialBpm, initialDelay }) => {
    const [bpm, setBpm] = useState(120);
    const [delay, setDelay] = useState();
    const [metronome, setMetronome] = useState(0);

    const handleBpmChange = useCallback((event) => {
        setBpm(event.target.value);
    });

    const submitBpmChange = useCallback(() => {
        initialBpm(bpm);
    }, [initialBpm, bpm]);

    const handleDelayChange = useCallback((event) => {
        setDelay(event.target.value);
    });

    const submitDelayChange = useCallback(() => {
        initialDelay(delay);
    }, [initialDelay, delay]);



    let metronomeIndex = 1;
    const interval = 60000 / bpm;
    const metronomeInterval = useRef(null);
    
    const startMetronome = () => { 
        setMetronome(1);
        metronomeInterval.current = setTimeout(setInterval(() => {
            if (metronomeIndex >= 4) {
                metronomeIndex = 0;
            }
            metronomeIndex++;
            setMetronome(metronomeIndex);

            console.log(metronomeIndex); // For debugging, remove later
        }, interval), delay)
        
    };
    
    const stopMetronome = () => {
        console.log(metronomeInterval.current);
        //clearTimeout(metronomeInterval.current);
        //clearInterval(metronomeInterval.current);
        metronomeInterval.current = null;
        setMetronome(0);
    }




    return (
        <div>
            <div className="ControlButtons">
                <button id="PlayButton" onClick={startMetronome}/><button id="StopButton" onClick={stopMetronome}/>
            </div>
            <div className={"metronome" + metronome} />
            <div className='settings'>
                <div className='settingbars'>
                    <input id="settings" className="text" placeholder='Insert BPM...' onChange={handleBpmChange}/><button className="text" id="settingbutton" onClick={submitBpmChange}>Set</button>
                </div>
                <div className='settingbars'>
                    <input id="settings" className="text" placeholder='Insert delay (MS)...' onChange={handleDelayChange}/><button className="text" id="settingbutton" onClick={submitDelayChange}>Set</button>
                </div>
            </div>
        </div>
    );
}

export default Metronome;
