import React, { useState, useCallback } from "react";
import ReactPlayer from 'react-player';
import "./VideoPlayer.css";
import Metronome from "../Metronome/Metronome"

const VideoPlayer = ({ videoUrl }) => {

    const [bpm, setBpm] = useState(120);
    const [delay, setDelay] = useState();
    var [playback, setPlayback] = useState(false); // default true
    var [startTime, setStartTime] = useState(1);

    function changeBpm(result) {
        setBpm(result);
    }

    function changeDelay(result) {
        setDelay(result);
    }
    
    function handlePlayback(result) {
        setPlayback(result);
        setTimeout(() => {
            setStartTime(0);
        }, 1000)
    }

    function isReady() {
        setPlayback(true);
    }




    

    return (
        <div id="VideoControls">
            <ReactPlayer
                config={{
                    youtube: {
                        playerVars: {
                            autoplay: 1,
                            start: startTime
                        }
                    }
                }}
                controls
                width="900px" height="540px"
                url={videoUrl}
                onReady={isReady}
                onStart={handlePlayback}
                muted={!playback}
                volume={0.5}
                playing={playback}
            />
            <div>
                <Metronome initialBpm={changeBpm} initialDelay={changeDelay} playback={handlePlayback} />
            </div>
            
        </div>
        
    )
}

export default VideoPlayer;
