import React, { useState, useCallback } from "react";
import "./Metronome.css";

const Metronome = ({ initialBpm, initialDelay }) => {
    const [bpm, setBpm] = useState();
    const [delay, setDelay] = useState();


    const handleBpmChange = useCallback((event) => {
        setBpm(event.target.value);
    });

    const submitBpmChange = useCallback(() => {
        initialBpm(bpm);
    }, []);

    const handleDelayChange = useCallback((event) => {
        setDelay(event.target.value);
    });

    const submitDelayChange = useCallback(() => {
        initialDelay(delay);
    }, []);

    return (
        <div>
            <div className='metronome0' />
            <div className='settings'>
                <input id="settings" className="text" placeholder='Insert BPM...' /*onChange={handleBpmChange}*/onSearch={submitBpmChange}/>
                <input id="settings" className="text" placeholder='Insert delay (MS)...' /*onChange={handleDelayChange}*/onSearch={submitDelayChange}/>
            </div>
        </div>
    );
}

export default Metronome;
