import React, { useState, useCallback } from "react";
import "./Metronome.css";

const Metronome = (props) => {
    const [bpm, setBpm] = useState(100);
    const [delay, setDelay] = useState(0);

    const handleBpmChange = useCallback((event) => {
        setBpm(event.target.value)
    });

    const saveBpmChange = useCallback(() => {
        props.changeBpm(bpm);
    }, [props.changeBpm, bpm])

    const handleDelayChange = (event) => {
        setDelay(event.target.value);
    }

    return (
        <div>
            <div className='metronome0' />
            <div className='settings'>
                <input id="settings" className="text" placeholder='Insert BPM...' onChange={handleBpmChange}/><button/>
                <input id="settings" className="text" placeholder='Insert delay (MS)...' onChange={handleDelayChange}/><button/>
            </div>
        </div>
    );
}

export default Metronome;
