import React, { useState, useRef } from "react";
import ReactPlayer from 'react-player';
import "./VideoPlayer.css";
import Metronome from "../Metronome/Metronome"

const VideoPlayer = ({ videoUrl }) => {

    const [bpm, setBpm] = useState(120);
    const [delay, setDelay] = useState();
    const playerRef = useRef(null);
    var [playback, setPlayback] = useState(false); // default true

    function changeBpm(result) {
        setBpm(result);
    }

    function changeDelay(result) {
        setDelay(result);
    }
    
    function handlePlayback(result) {
        setPlayback(result);
    }

    function isReady() {
        setPlayback(true);
        
    }

    function setTimeToZero() {
        setTimeout(() => {
            playerRef.current.seekTo(0);
        }, 1000)
    }

    return (
        <div id="VideoControls">
            <ReactPlayer
                ref={playerRef}
                controls
                width="900px" height="540px"
                url={videoUrl}
                onReady={isReady}
                onStart={setTimeToZero}
                muted={!playback}
                volume={0.5}
                playing={playback}
            />
            <div>
                <Metronome initialBpm={changeBpm} initialDelay={changeDelay} playback={handlePlayback} />
            </div>
            
        </div>
        
    )
}

export default VideoPlayer;
